ref[0] = Referencia.Factory.newInstance() ref[0].setNroLinRef(1) ref[0].setTpoDocRef('SET') ref[0].setFolioRef(""+folio) ref[0].xsetFchRef(FechaType.Factory.newValue(Utilities.fechaFormat.format(new Date()))) //ref[0].setRazonRef("CASO 1889781-1") ref[0].setRazonRef(refCaso1)
doc.getDTE().getDocumento().setReferenciaArray(ref)
// Timbro
doc.getDTE().timbrar(caf.getCAF(), caf.getPrivateKey(null))
// antes de firmar le doy formato a los datos opts = new XmlOptions() opts.setSaveImplicitNamespaces(namespaces) opts.setLoadSubstituteNamespaces(namespaces) opts.setLoadAdditionalNamespaces(namespaces) opts.setSavePrettyPrint() opts.setSavePrettyPrintIndent(4)
