import cl.nic.dte.util.Signer import cl.nic.dte.util.Utilities import cl.nic.dte.util.XMLUtil import cl.sii.siiDte.AUTORIZACIONDocument import cl.sii.siiDte.AutorizacionType import cl.sii.siiDte.DTEDefType.Documento.Detalle import cl.sii.siiDte.DTEDefType.Documento.Encabezado.IdDoc import cl.sii.siiDte.DTEDefType.Documento.Encabezado.Receptor import cl.sii.siiDte.DTEDefType.Documento.Encabezado.Totales import cl.sii.siiDte.DTEDefType.Documento.Referencia import cl.sii.siiDte.DTEDefType.Documento.DscRcgGlobal import cl.sii.siiDte.DTEDefType.Documento.DscRcgGlobal.TpoMov.Enum import cl.sii.siiDte.DTEDocument import cl.sii.siiDte.FechaHoraType import cl.sii.siiDte.FechaType import cl.sii.siiDte.MedioPagoType import org.apache.commons.io.IOUtils
