logger.warn("Creando Guia Caso 2, con folio: " + dte2map.folio) /** Ejemplo de Caso 2 ****************************************************
CASO 1965340-2 ============== DOCUMENTO GUIA DE DESPACHO MOTIVO: VENTA TRASLADO POR: EMISOR DEL DOCUMENTO AL LOCAL DEL CLIENTE
ITEM CANTIDAD PRECIO UNITARIO ITEM 1 410 8270 ITEM 2 796 1785
*************************************************************************/ tipoFactura = Integer.valueOf('52') if(formaPago != null) frmPago = Integer.valueOf(formaPago)
// Debo meter el namespace porque SII no lo genera HashMap<String, String> namespaces2 = new HashMap<String, String>() namespaces2.put("", "http://www.sii.cl/SiiDte") namespaces2.put("xmlns:xsi", "http://www.w3.org/2001/XMLSchema-instance") XmlOptions opts2 = new XmlOptions() opts2.setLoadSubstituteNamespaces(namespaces2)
// Recuperación de archivo CAF desde BD AutorizacionType caf2 = AUTORIZACIONDocument.Factory.parse(new ByteArrayInputStream(dte2map.cafData.getBytes()), opts2).getAUTORIZACION()
// Construyo base a partir del template DTEDocument doc2 = DTEDocument.Factory.parse(ec.resource.getLocationStream(plantillaS), opts2)
// leo certificado y llave privada del archivo pkcs12 KeyStore ks = KeyStore.getInstance("PKCS12") ks.load(new ByteArrayInputStream(certData.decodeBase64()), passCert.toCharArray()) String alias = ks.aliases().nextElement()
cert = (X509Certificate) ks.getCertificate(alias) key = (PrivateKey) ks.getKey(alias, passCert.toCharArray())
// Se recorre lista de productos para armar documento (detailList)
iddoc = doc2.getDTE().getDocumento().getEncabezado().addNewIdDoc() iddoc.setFolio(folio) iddoc.setTipoDespacho(2) iddoc.setIndTraslado(1) // Obtención de ID distinto doc2.getDTE().getDocumento().setID("N" + System.nanoTime())
// Tipo de DTE iddoc.setTipoDTE(BigInteger.valueOf(tipoFactura)) iddoc.xsetFchEmis(FechaType.Factory.newValue(Utilities.fechaFormat.format(new Date())))
formatterFechaEmision = new SimpleDateFormat("yyyy-MM-dd") dateFechaEmision = new Date() fechaEmision = formatterFechaEmision.format(dateFechaEmision)
cal = Calendar.getInstance() cal.add(Calendar.DAY_OF_MONTH, 45) iddoc.xsetFchCancel(FechaType.Factory.newValue(Utilities.fechaFormat.format(new Date()))) // Medio y forma de pago if (medioPago != null ) { iddoc.setMedioPago(MedioPagoType.Enum.forString(medioPago)) } else { iddoc.setMedioPago(MedioPagoType.Enum.forString("CH")) } iddoc.setFmaPago(BigInteger.valueOf(frmPago))
// Si es guía de despacho se configura indicador de traslado
// Receptor Receptor recp2 = doc2.getDTE().getDocumento().getEncabezado().addNewReceptor()
// Usamos de receptor a Entel
recp2.setRUTRecep("79693930-3") recp2.setRznSocRecep("LOREAL") recp2.setGiroRecep("COMERCIALIZ. PROD. FARMAC. Y COSMETICOS") recp2.setContacto("224406777") recp2.setDirRecep("Av. Apoquindo 3885 Of. 201") recp2.setCmnaRecep("LAS CONDES") recp2.setCiudadRecep("SANTIAGO")
Detalle[] det2 = new Detalle[2]
// Agrego detalle 0 det2[0] = Detalle.Factory.newInstance() det2[0].setNroLinDet(1) det2[0].setNmbItem("ITEM 1") det2[0].setQtyItem(BigDecimal.valueOf(qtyItem1Caso2)) det2[0].setPrcItem(BigDecimal.valueOf(prcItem1Caso2)) totalItem1 = Math.round(qtyItem1Caso2*prcItem1Caso2) det2[0].setMontoItem(totalItem1)
det2[1] = Detalle.Factory.newInstance() det2[1].setNroLinDet(2) det2[1].setNmbItem("ITEM 2") det2[1].setQtyItem(BigDecimal.valueOf(qtyItem2Caso2)) det2[1].setPrcItem(BigDecimal.valueOf(prcItem2Caso2)) totalItem2 = Math.round(qtyItem2Caso2*prcItem2Caso2) det2[1].setMontoItem(totalItem2)
doc2.getDTE().getDocumento().setDetalleArray(det2)
// Totales Totales tot2 = doc2.getDTE().getDocumento().getEncabezado().addNewTotales() totalNeto = totalItem1 + totalItem2 tot2.setMntNeto(Math.round(totalNeto)) tot2.setTasaIVA(BigDecimal.valueOf(19)) // Valor de solo IVA totalIVA = Math.round(totalNeto * 0.19) tot2.setIVA(totalIVA) // total neto + IVA totalInvoice = totalNeto + totalIVA tot2.setMntTotal(Math.round(totalInvoice)) amount = totalInvoice
// Referencias Referencia[] ref2 = new Referencia[1]
ref2[0] = Referencia.Factory.newInstance() ref2[0].setNroLinRef(1) ref2[0].setTpoDocRef('SET') ref2[0].setFolioRef(""+folio) ref2[0].xsetFchRef(FechaType.Factory.newValue(Utilities.fechaFormat.format(new Date()))) ref2[0].setRazonRef(refCaso2)
doc2.getDTE().getDocumento().setReferenciaArray(ref2)
// Timbro doc2.getDTE().timbrar(caf2.getCAF(), caf2.getPrivateKey(null))
// antes de firmar le doy formato a los datos opts = new XmlOptions() opts.setSaveImplicitNamespaces(namespaces2) opts.setLoadSubstituteNamespaces(namespaces2) opts.setLoadAdditionalNamespaces(namespaces2) opts.setSavePrettyPrint() opts.setSavePrettyPrintIndent(4)
// releo el doc para que se reflejen los cambios de formato doc2 = DTEDocument.Factory.parse(doc2.newInputStream(opts), opts)
// Guardo opts = new XmlOptions() opts.setCharacterEncoding("ISO-8859-1") opts.setSaveImplicitNamespaces(namespaces2)
uri = "" now = FechaHoraType.Factory.newValue(Utilities.fechaHoraFormat.format(new Date()))
if (doc2.getDTE().isSetDocumento()) { uri = doc2.getDTE().getDocumento().getID() doc2.getDTE().getDocumento().xsetTmstFirma(now) } else if (doc2.getDTE().isSetLiquidacion()) { uri = doc2.getDTE().getLiquidacion().getID() doc2.getDTE().getLiquidacion().xsetTmstFirma(now) } else if (doc2.getDTE().isSetExportaciones()) { uri = doc2.getDTE().getExportaciones().getID() doc2.getDTE().getExportaciones().xsetTmstFirma(now) }
uri = "#" + uri out = new ByteArrayOutputStream() doc2.save(out, opts) Document doc22 = MoquiDTEUtils.parseDocument(out.toByteArray()) byte[] salida2 = MoquiDTEUtils.sign(doc22, uri, key, cert, uri, "Documento") doc22 = MoquiDTEUtils.parseDocument(salida2)
if (Signer.verify(doc22, "Documento")) { Path path = Paths.get(resultS + "DTE" + tipoFactura + "-" + folio + ".xml") Files.write(path, salida2) logger.warn("Factura "+path+" folio "+folio+" generada OK") } else { Path path = Paths.get(resultS + "DTE" + tipoFactura + "-" + folio + "-mala.xml") Files.write(path, salida2) logger.warn("Error al generar factura folio "+folio) }

file = new File(pdf)
fis = null
bArray = new byte[(int) file.length()]
try {
fis = new FileInputStream(file)
fis.read(bArray)
fis.close()
} catch (IOException ioExp) {
ioExp.printStackTrace()
}
logger.warn("pdf largo: " + file.length())
bArray2 = bArray

fileCedible = new File(pdfCedible)
fisCedible = null
bArrayCedible = new byte[(int) fileCedible.length()]
try {
fisCedible = new FileInputStream(fileCedible)
fisCedible.read(bArrayCedible)
fisCedible.close()
} catch (IOException ioExp) {
ioExp.printStackTrace()
}

